using System.Collections.Generic;using System.Text.RegularExpressions;using TestDataGenerator.Models;namespace TestDataGenerator{    public class TestSuiteInfo    {        public string Name { get; set; }        public string Target { get; set; }        public List<TestCaseInfo> TestCases { get; set; } = new List<TestCaseInfo>();        public TestSummeryInfo TestSummery { get; set; } = new TestSummeryInfo();        //得到被测函数名（如：TriangleType）        public string FunctionName        {            get            {                var pattern = @"\.(\w+)";                var match = Regex.Match(Target, pattern);                var methodName = match.Groups[1].Value;                return methodName;            }        }    }}